          as.list() |&gt; 
          as.data.frame() |&gt; 
          cbind.data.frame(data.frame(game = as.integer(game)))
      }) |&gt; 
      dplyr::bind_rows()
  }) |&gt; 
  dplyr::bind_rows() |&gt; 
  dplyr::select(game, red, green, blue) |&gt; 
  dplyr::group_by(game) |&gt; 
  dplyr::summarise(
    power = max(red, na.rm = TRUE) * 
      max(green, na.rm = TRUE) *
      max(blue, na.rm = TRUE)
  ) |&gt; 
  dplyr::pull(power) |&gt; 
  sum()
`)







